instructions = """



"""

keyCode = 5
for line in instructions.split('\n'):
    for c in line:
        if c == 'L' and keyCode % 3 != 1:
            keyCode -= 1
        if c == 'R' and keyCode % 3 != 0:
            keyCode += 1
        if c == 'U' and keyCode > 3:
            keyCode -= 3
        if c == 'D' and keyCode < 7:
            keyCode += 3
    print(keyCode)
